export function getQuotes(category = 'all', searchTerm = '') {
  let result = category === 'all' ? quotes : quotes.filter(q => q.category === category);
  if (searchTerm.trim()) {
    const term = searchTerm.toLowerCase();
    result = result.filter(
      q => q.text.toLowerCase().includes(term) || q.author.toLowerCase().includes(term)
    );
  }
  return result;
}

export default quotes;
